--[[动态子项--]]local kd = KDGame;local gDef = gDef;local gSink = A_ViewManager;DynamicItem = kd.inherit(kd.Layer);local impl = DynamicItem;local ScreenW,ScreenH = kd.SceneSize.width,kd.SceneSize.high;local idsw ={	--/* Custom ID */	ID_CUS_ML_MAIN2_LM           = 6001,}function impl:init()	self.m_thView = kd.class(kd.ResManage, gDef.GetResPath("ResAll/UI/XQDongTai.UI"), self);	if (self.m_thView) then		self:addChild(self.m_thView);	end	-- 图片	self.m_cusPhoto = gDef:SetFaceForGame(self,idsw.ID_CUS_ML_MAIN2_LM,nil,false,3)endfunction impl:SetViewData(photo)	self.m_cusPhoto:SetFace(photo);endfunction impl:GetWH()	local _,_,w,h = self.m_thView:GetScaleRect(idsw.ID_CUS_ML_MAIN2_LM);	return w,h;end